import React, { useState, useEffect } from 'react';
import { Calendar, Clock, DollarSign, Coffee, Trash2, Save, X, LogIn, LogOut, Minus, ArrowLeft, User, Settings, BarChart3, Users, Download } from 'lucide-react';

// Mock Firebase Auth (replace with actual Firebase implementation)
const mockFirebaseAuth = {
  currentUser: null,
  signInWithGoogle: () => Promise.resolve({
    user: {
      uid: 'mock-user-123',
      displayName: 'John Doe',
      email: 'john.doe@gmail.com',
      photoURL: 'https://via.placeholder.com/40'
    }
  }),
  signOut: () => Promise.resolve(),
  onAuthStateChanged: (callback) => {
    // Mock auth state change
    setTimeout(() => {
      callback(null); // Start logged out
    }, 100);
  }
};

// Mock Firebase Firestore (replace with actual Firebase implementation)
const mockFirestore = {
  saveUserData: (userId, data) => {
    localStorage.setItem(`workq_user_${userId}`, JSON.stringify(data));
    return Promise.resolve();
  },
  getUserData: (userId) => {
    const data = localStorage.getItem(`workq_user_${userId}`);
    return Promise.resolve(data ? JSON.parse(data) : null);
  }
};

const WorkTimeCalculator = () => {
  // Auth State
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // App State
  const [currentView, setCurrentView] = useState('timetracker'); // 'timetracker', 'dashboard', 'settings'
  const [currentDate, setCurrentDate] = useState(new Date());
  const [workRecords, setWorkRecords] = useState({});
  const [leaveRecords, setLeaveRecords] = useState({});
  const [salaryPerHour, setSalaryPerHour] = useState(500);
  const [showSalarySettings, setShowSalarySettings] = useState(false);

  // Time entry form state with AM/PM format
  const [timeEntry, setTimeEntry] = useState({
    selectedDate: '',
    inTime: { hour: '', minute: '', period: 'AM' },
    outTime: { hour: '', minute: '', period: 'PM' },
    breakTime: 60
  });

  // Track which times have been set via buttons
  const [timeStatus, setTimeStatus] = useState({
    inTimeSet: false,
    outTimeSet: false
  });

  // Modal state for date details
  const [selectedDateModal, setSelectedDateModal] = useState({
    isOpen: false,
    date: '',
    dateStr: ''
  });

  // Initialize auth listener
  useEffect(() => {
    const unsubscribe = mockFirebaseAuth.onAuthStateChanged((user) => {
      setUser(user);
      setIsLoading(false);
      if (user) {
        loadUserData(user.uid);
      }
    });

    return () => unsubscribe && unsubscribe();
  }, []);

  // Auto-fill current date on component mount
  useEffect(() => {
    const now = new Date();
    const currentDateStr = now.toISOString().split('T')[0];

    setTimeEntry(prev => ({
      ...prev,
      selectedDate: currentDateStr
    }));
  }, []);

  // Save data when workRecords or leaveRecords change
  useEffect(() => {
    if (user) {
      saveUserData();
    }
  }, [workRecords, leaveRecords, salaryPerHour, user]);

  // Auth Functions
  const signInWithGoogle = async () => {
    try {
      const result = await mockFirebaseAuth.signInWithGoogle();
      setUser(result.user);
    } catch (error) {
      console.error('Error signing in:', error);
      alert('Failed to sign in with Google');
    }
  };

  const signOut = async () => {
    try {
      await mockFirebaseAuth.signOut();
      setUser(null);
      setWorkRecords({});
      setLeaveRecords({});
      setSalaryPerHour(500);
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  // Data Management Functions
  const saveUserData = async () => {
    if (!user) return;

    try {
      const userData = {
        workRecords,
        leaveRecords,
        salaryPerHour,
        lastUpdated: new Date().toISOString()
      };
      await mockFirestore.saveUserData(user.uid, userData);
    } catch (error) {
      console.error('Error saving data:', error);
    }
  };

  const loadUserData = async (userId) => {
    try {
      const userData = await mockFirestore.getUserData(userId);
      if (userData) {
        setWorkRecords(userData.workRecords || {});
        setLeaveRecords(userData.leaveRecords || {});
        setSalaryPerHour(userData.salaryPerHour || 500);
      }
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  // Convert 12-hour time object to 24-hour format string
  const timeObjectTo24Hour = (timeObj) => {
    if (!timeObj.hour || !timeObj.minute) return '';
    let hour = parseInt(timeObj.hour);
    if (timeObj.period === 'PM' && hour !== 12) hour += 12;
    if (timeObj.period === 'AM' && hour === 12) hour = 0;
    return `${hour.toString().padStart(2, '0')}:${timeObj.minute}`;
  };

  // Convert 24-hour format to 12-hour time object
  const time24HourToObject = (time24) => {
    if (!time24) return { hour: '', minute: '', period: 'AM' };
    const [hours, minutes] = time24.split(':');
    const hour = parseInt(hours);
    const period = hour >= 12 ? 'PM' : 'AM';
    const hour12 = hour % 12 || 12;
    return {
      hour: hour12.toString().padStart(2, '0'),
      minute: minutes,
      period: period
    };
  };

  // Format time object to display string
  const formatTimeDisplay = (timeObj) => {
    if (!timeObj.hour || !timeObj.minute) return '';
    return `${timeObj.hour}:${timeObj.minute} ${timeObj.period}`;
  };

  // Get current time as time object
  const getCurrentTimeObject = () => {
    const now = new Date();
    const hours = now.getHours();
    const minutes = now.getMinutes();
    const period = hours >= 12 ? 'PM' : 'AM';
    const hour12 = hours % 12 || 12;
    return {
      hour: hour12.toString().padStart(2, '0'),
      minute: minutes.toString().padStart(2, '0'),
      period: period
    };
  };

  // Get current time display string
  const getCurrentTimeDisplay = () => {
    const timeObj = getCurrentTimeObject();
    return formatTimeDisplay(timeObj);
  };

  // Handle automatic in time entry
  const handleInTimeEntry = () => {
    const currentTimeObj = getCurrentTimeObject();
    setTimeEntry(prev => ({
      ...prev,
      inTime: currentTimeObj
    }));
    setTimeStatus(prev => ({
      ...prev,
      inTimeSet: true
    }));
  };

  // Handle automatic out time entry
  const handleOutTimeEntry = () => {
    const currentTimeObj = getCurrentTimeObject();
    setTimeEntry(prev => ({
      ...prev,
      outTime: currentTimeObj
    }));
    setTimeStatus(prev => ({
      ...prev,
      outTimeSet: true
    }));
  };

  // Format currency with rupees and paise
  const formatCurrency = (amount) => {
    const rupees = Math.floor(amount);
    const paise = Math.round((amount - rupees) * 100);
    if (paise === 0) {
      return `₹${rupees.toLocaleString('en-IN')}`;
    }
    return `₹${rupees.toLocaleString('en-IN')}.${paise.toString().padStart(2, '0')}`;
  };

  // Get current month and year
  const currentMonth = currentDate.getMonth();
  const currentYear = currentDate.getFullYear();

  // Generate calendar days
  const getDaysInMonth = (month, year) => {
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];

    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      days.push(day);
    }

    return days;
  };

  const formatDate = (day) => {
    if (!day) return '';
    return `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
  };

  const calculateWorkingHours = (inTimeObj, outTimeObj, breakMinutes = 60) => {
    const inTime24 = timeObjectTo24Hour(inTimeObj);
    const outTime24 = timeObjectTo24Hour(outTimeObj);

    if (!inTime24 || !outTime24) return 0;

    const inDate = new Date(`2000-01-01T${inTime24}`);
    const outDate = new Date(`2000-01-01T${outTime24}`);

    if (outDate < inDate) {
      outDate.setDate(outDate.getDate() + 1);
    }

    const diffMs = outDate - inDate;
    const diffHours = diffMs / (1000 * 60 * 60);
    const breakHours = breakMinutes / 60;

    return Math.max(0, diffHours - breakHours);
  };

  const handleTimeSubmit = () => {
    if (!timeEntry.selectedDate || !timeEntry.inTime.hour || !timeEntry.outTime.hour) {
      alert('Please fill in all required fields (Date, In Time, Out Time)');
      return;
    }

    const workingHours = calculateWorkingHours(timeEntry.inTime, timeEntry.outTime, timeEntry.breakTime);

    setWorkRecords(prev => ({
      ...prev,
      [timeEntry.selectedDate]: {
        inTime: timeEntry.inTime,
        outTime: timeEntry.outTime,
        breakTime: timeEntry.breakTime,
        workingHours: workingHours
      }
    }));

    setTimeEntry(prev => ({
      ...prev,
      inTime: { hour: '', minute: '', period: 'AM' },
      outTime: { hour: '', minute: '', period: 'PM' },
      breakTime: 60
    }));

    setTimeStatus({
      inTimeSet: false,
      outTimeSet: false
    });

    alert('Work time added successfully!');
  };

  const handleDateClick = (day) => {
    if (!day) return;
    const dateStr = formatDate(day);
    const date = new Date(currentYear, currentMonth, day);
    const formattedDate = date.toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });

    setSelectedDateModal({
      isOpen: true,
      date: formattedDate,
      dateStr: dateStr
    });
  };

  const handleLeaveToggle = (dateStr) => {
    setLeaveRecords(prev => ({
      ...prev,
      [dateStr]: !prev[dateStr]
    }));
  };

  const deleteWorkRecord = (dateStr) => {
    setWorkRecords(prev => {
      const newRecords = { ...prev };
      delete newRecords[dateStr];
      return newRecords;
    });
  };

  const editWorkRecord = (dateStr) => {
    const record = workRecords[dateStr];
    if (record) {
      setTimeEntry({
        selectedDate: dateStr,
        inTime: record.inTime,
        outTime: record.outTime,
        breakTime: record.breakTime
      });
      setTimeStatus({
        inTimeSet: true,
        outTimeSet: true
      });
      setCurrentView('timetracker');
    }
  };

  const getTotalHours = () => {
    return Object.values(workRecords).reduce((total, record) => total + record.workingHours, 0);
  };

  const getTotalSalary = () => {
    return getTotalHours() * salaryPerHour;
  };

  const getWorkingDays = () => {
    return Object.keys(workRecords).length;
  };

  const getLeaveDays = () => {
    return Object.values(leaveRecords).filter(Boolean).length;
  };

  const getMonthlyStats = () => {
    const currentMonthStr = `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}`;
    const monthlyRecords = Object.entries(workRecords).filter(([date]) => 
      date.startsWith(currentMonthStr)
    );

    const monthlyHours = monthlyRecords.reduce((total, [, record]) => total + record.workingHours, 0);
    const monthlySalary = monthlyHours * salaryPerHour;
    const monthlyDays = monthlyRecords.length;

    return { monthlyHours, monthlySalary, monthlyDays };
  };

  const exportData = () => {
    const data = {
      workRecords,
      leaveRecords,
      salaryPerHour,
      totalStats: {
        totalHours: getTotalHours(),
        totalSalary: getTotalSalary(),
        workingDays: getWorkingDays(),
        leaveDays: getLeaveDays()
      },
      exportDate: new Date().toISOString()
    };

    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `workq-data-${user.displayName.replace(/\s+/g, '-')}-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const days = getDaysInMonth(currentMonth, currentYear);

  // Loading screen
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <h2 className="text-xl font-semibold text-gray-700">Loading WorkQ...</h2>
        </div>
      </div>
    );
  }

  // Login screen
  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <div className="bg-white rounded-2xl shadow-xl p-8 text-center">
            <div className="mb-8">
              <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-2">
                WorkQ
              </h1>
              <p className="text-gray-600">Smart Time Tracking & Salary Calculator</p>
            </div>

            <div className="mb-8">
              <div className="bg-blue-50 rounded-lg p-4 mb-4">
                <h3 className="font-semibold text-blue-800 mb-2">Features</h3>
                <ul className="text-sm text-blue-600 space-y-1">
                  <li>• Track work hours with clock in/out</li>
                  <li>• Calculate salary automatically</li>
                  <li>• Visual calendar view</li>
                  <li>• Leave management</li>
                  <li>• Export your data</li>
                </ul>
              </div>
            </div>

            <button
              onClick={signInWithGoogle}
              className="w-full bg-white border-2 border-gray-300 rounded-lg px-6 py-3 flex items-center justify-center gap-3 hover:bg-gray-50 transition-all duration-200 shadow-md hover:shadow-lg"
            >
              <svg className="w-5 h-5" viewBox="0 0 24 24">
                <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
              <span className="font-medium text-gray-700">Continue with Google</span>
            </button>

            <div className="mt-6 text-xs text-gray-500">
              <p>Secure authentication powered by Google</p>
              <p className="mt-1">Your data is stored safely and privately</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

