import React, { useState, useEffect } from 'react';
import { Calendar, Clock, DollarSign, Coffee, Trash2, Save, X, LogIn, LogOut, Minus, User, Settings, BarChart3, Download, ArrowLeft } from 'lucide-react';

// Google OAuth Configuration - Replace with your actual client ID
const GOOGLE_CLIENT_ID = 'YOUR_GOOGLE_CLIENT_ID.apps.googleusercontent.com';

// Demo mode configuration for testing
const DEMO_MODE = true; // Set to false when you have a real Google Client ID

// Google Authentication Helper Functions
const loadGoogleScript = () => {
  return new Promise((resolve, reject) => {
    if (window.google) {
      resolve(window.google);
      return;
    }

    const script = document.createElement('script');
    script.src = 'https://accounts.google.com/gsi/client';
    script.async = true;
    script.defer = true;
    script.onload = () => {
      if (window.google) {
        resolve(window.google);
      } else {
        reject(new Error('Google script loaded but google object not found'));
      }
    };
    script.onerror = () => reject(new Error('Failed to load Google script'));
    document.head.appendChild(script);
  });
};

// JWT Token decoder
const decodeJWT = (token) => {
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );
    return JSON.parse(jsonPayload);
  } catch (error) {
    console.error('Error decoding JWT:', error);
    return null;
  }
};

// Session Storage Helper (using sessionStorage instead of localStorage for better security)
const sessionHelper = {
  saveUserSession: (userData) => {
    try {
      sessionStorage.setItem('workq_session', JSON.stringify(userData));
      return true;
    } catch (error) {
      console.error('Error saving session:', error);
      return false;
    }
  },

  getUserSession: () => {
    try {
      const session = sessionStorage.getItem('workq_session');
      return session ? JSON.parse(session) : null;
    } catch (error) {
      console.error('Error loading session:', error);
      return null;
    }
  },

  clearUserSession: () => {
    sessionStorage.removeItem('workq_session');
  },

  saveUserData: (userId, data) => {
    try {
      sessionStorage.setItem(`workq_data_${userId}`, JSON.stringify(data));
      return true;
    } catch (error) {
      console.error('Error saving user data:', error);
      return false;
    }
  },

  getUserData: (userId) => {
    try {
      const data = sessionStorage.getItem(`workq_data_${userId}`);
      return data ? JSON.parse(data) : null;
    } catch (error) {
      console.error('Error loading user data:', error);
      return null;
    }
  }
};

const WorkTimeCalculator = () => {
  // Auth State
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [googleLoaded, setGoogleLoaded] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // App State
  const [currentView, setCurrentView] = useState('timetracker');
  const [currentDate, setCurrentDate] = useState(new Date());
  const [workRecords, setWorkRecords] = useState({});
  const [leaveRecords, setLeaveRecords] = useState({});
  const [salaryPerHour, setSalaryPerHour] = useState(500);
  const [showSalarySettings, setShowSalarySettings] = useState(false);

  // Time entry form state
  const [timeEntry, setTimeEntry] = useState({
    selectedDate: '',
    inTime: { hour: '', minute: '', period: 'AM' },
    outTime: { hour: '', minute: '', period: 'PM' },
    breakTime: 60
  });

  const [timeStatus, setTimeStatus] = useState({
    inTimeSet: false,
    outTimeSet: false
  });

  const [selectedDateModal, setSelectedDateModal] = useState({
    isOpen: false,
    date: '',
    dateStr: ''
  });

  // Initialize Google OAuth
  useEffect(() => {
    const initializeGoogle = async () => {
      try {
        // Check for existing session first
        const savedSession = sessionHelper.getUserSession();
        if (savedSession && savedSession.user && savedSession.tokenExpiry > Date.now()) {
          setUser(savedSession.user);
          setIsAuthenticated(true);
          await loadUserData(savedSession.user.uid);
          setIsLoading(false);
          return;
        }

        // If demo mode is enabled, skip Google OAuth initialization
        if (DEMO_MODE || GOOGLE_CLIENT_ID.includes('YOUR_GOOGLE_CLIENT_ID')) {
          setGoogleLoaded(true);
          setIsLoading(false);
          return;
        }

        const google = await loadGoogleScript();
        setGoogleLoaded(true);

        // Initialize Google OAuth
        google.accounts.id.initialize({
          client_id: GOOGLE_CLIENT_ID,
          callback: handleGoogleResponse,
          auto_select: false,
          cancel_on_tap_outside: true
        });

      } catch (error) {
        console.error('Failed to initialize Google OAuth:', error);
        // Fallback to demo mode if Google OAuth fails
        setGoogleLoaded(true);
      } finally {
        setIsLoading(false);
      }
    };

    initializeGoogle();
  }, []);

  // Auto-fill current date
  useEffect(() => {
    const now = new Date();
    const currentDateStr = now.toISOString().split('T')[0];
    setTimeEntry(prev => ({ ...prev, selectedDate: currentDateStr }));
  }, []);

  // Save data when records change (only if authenticated)
  useEffect(() => {
    if (user && isAuthenticated) {
      saveUserData();
    }
  }, [workRecords, leaveRecords, salaryPerHour, user, isAuthenticated]);

  // Google OAuth Response Handler
  const handleGoogleResponse = async (response) => {
    try {
      const userInfo = decodeJWT(response.credential);

      if (userInfo) {
        const userProfile = {
          uid: userInfo.sub,
          displayName: userInfo.name,
          email: userInfo.email,
          photoURL: userInfo.picture,
          token: response.credential
        };

        // Calculate token expiry (Google JWT tokens are typically valid for 1 hour)
        const tokenExpiry = Date.now() + (60 * 60 * 1000); // 1 hour from now

        const sessionData = {
          user: userProfile,
          tokenExpiry: tokenExpiry,
          loginTime: Date.now()
        };

        setUser(userProfile);
        setIsAuthenticated(true);

        // Save session
        sessionHelper.saveUserSession(sessionData);

        // Load user data
        await loadUserData(userProfile.uid);
      }
    } catch (error) {
      console.error('Error handling Google response:', error);
      alert('Failed to sign in with Google. Please try again.');
    }
  };

  // Sign in with Google or Demo
  const signInWithGoogle = () => {
    // Demo mode sign in
    if (DEMO_MODE || GOOGLE_CLIENT_ID.includes('YOUR_GOOGLE_CLIENT_ID')) {
      signInWithDemo();
      return;
    }

    if (!googleLoaded || !window.google) {
      alert('Google OAuth is still loading. Please wait a moment and try again.');
      return;
    }

    try {
      window.google.accounts.id.prompt((notification) => {
        if (notification.isNotDisplayed() || notification.isSkippedMoment()) {
          // Show the sign-in button if prompt fails
          const buttonDiv = document.getElementById('google-signin-button');
          if (buttonDiv) {
            window.google.accounts.id.renderButton(buttonDiv, {
              theme: 'outline',
              size: 'large',
              width: '100%',
              text: 'continue_with',
              shape: 'rectangular'
            });
          }
        }
      });
    } catch (error) {
      console.error('Error initiating Google sign in:', error);
      // Fallback to demo mode
      signInWithDemo();
    }
  };

  // Demo sign in function
  const signInWithDemo = () => {
    const demoUser = {
      uid: 'demo-user-' + Date.now(),
      displayName: 'Demo User',
      email: 'demo@workq.com',
      photoURL: 'https://via.placeholder.com/40?text=Demo'
    };

    const sessionData = {
      user: demoUser,
      tokenExpiry: Date.now() + (24 * 60 * 60 * 1000), // 24 hours
      loginTime: Date.now(),
      isDemoMode: true
    };

    setUser(demoUser);
    setIsAuthenticated(true);
    sessionHelper.saveUserSession(sessionData);
    loadUserData(demoUser.uid);
  };

  // Sign out
  const signOut = async () => {
    try {
      if (window.google?.accounts?.id) {
        window.google.accounts.id.disableAutoSelect();
      }

      // Clear all session data
      sessionHelper.clearUserSession();
      if (user) {
        sessionStorage.removeItem(`workq_data_${user.uid}`);
      }

      setUser(null);
      setIsAuthenticated(false);
      setWorkRecords({});
      setLeaveRecords({});
      setSalaryPerHour(500);
      setCurrentView('timetracker');
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  // Data Management
  const saveUserData = async () => {
    if (!user || !isAuthenticated) return;

    try {
      const userData = {
        workRecords,
        leaveRecords,
        salaryPerHour,
        lastUpdated: new Date().toISOString()
      };
      sessionHelper.saveUserData(user.uid, userData);
    } catch (error) {
      console.error('Error saving data:', error);
    }
  };

  const loadUserData = async (userId) => {
    try {
      const userData = sessionHelper.getUserData(userId);
      if (userData) {
        setWorkRecords(userData.workRecords || {});
        setLeaveRecords(userData.leaveRecords || {});
        setSalaryPerHour(userData.salaryPerHour || 500);
      }
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  // Return to index.html function
  const returnToIndex = () => {
    window.location.href = 'index.html';
  };

  // Time utility functions
  const timeObjectTo24Hour = (timeObj) => {
    if (!timeObj.hour || !timeObj.minute) return '';
    let hour = parseInt(timeObj.hour);
    if (timeObj.period === 'PM' && hour !== 12) hour += 12;
    if (timeObj.period === 'AM' && hour === 12) hour = 0;
    return `${hour.toString().padStart(2, '0')}:${timeObj.minute}`;
  };

  const formatTimeDisplay = (timeObj) => {
    if (!timeObj.hour || !timeObj.minute) return '';
    return `${timeObj.hour}:${timeObj.minute} ${timeObj.period}`;
  };

  const getCurrentTimeObject = () => {
    const now = new Date();
    const hours = now.getHours();
    const minutes = now.getMinutes();
    const period = hours >= 12 ? 'PM' : 'AM';
    const hour12 = hours % 12 || 12;
    return {
      hour: hour12.toString().padStart(2, '0'),
      minute: minutes.toString().padStart(2, '0'),
      period: period
    };
  };

  const getCurrentTimeDisplay = () => {
    const timeObj = getCurrentTimeObject();
    return formatTimeDisplay(timeObj);
  };

  const handleInTimeEntry = () => {
    const currentTimeObj = getCurrentTimeObject();
    setTimeEntry(prev => ({ ...prev, inTime: currentTimeObj }));
    setTimeStatus(prev => ({ ...prev, inTimeSet: true }));
  };

  const handleOutTimeEntry = () => {
    const currentTimeObj = getCurrentTimeObject();
    setTimeEntry(prev => ({ ...prev, outTime: currentTimeObj }));
    setTimeStatus(prev => ({ ...prev, outTimeSet: true }));
  };

  const formatCurrency = (amount) => {
    const rupees = Math.floor(amount);
    const paise = Math.round((amount - rupees) * 100);
    if (paise === 0) {
      return `₹${rupees.toLocaleString('en-IN')}`;
    }
    return `₹${rupees.toLocaleString('en-IN')}.${paise.toString().padStart(2, '0')}`;
  };

  // Calendar functions
  const currentMonth = currentDate.getMonth();
  const currentYear = currentDate.getFullYear();

  const getDaysInMonth = (month, year) => {
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(day);
    }
    return days;
  };

  const formatDate = (day) => {
    if (!day) return '';
    return `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
  };

  const calculateWorkingHours = (inTimeObj, outTimeObj, breakMinutes = 60) => {
    const inTime24 = timeObjectTo24Hour(inTimeObj);
    const outTime24 = timeObjectTo24Hour(outTimeObj);

    if (!inTime24 || !outTime24) return 0;

    const inDate = new Date(`2000-01-01T${inTime24}`);
    const outDate = new Date(`2000-01-01T${outTime24}`);

    if (outDate < inDate) {
      outDate.setDate(outDate.getDate() + 1);
    }

    const diffMs = outDate - inDate;
    const diffHours = diffMs / (1000 * 60 * 60);
    const breakHours = breakMinutes / 60;

    return Math.max(0, diffHours - breakHours);
  };

  const handleTimeSubmit = () => {
    if (!timeEntry.selectedDate || !timeEntry.inTime.hour || !timeEntry.outTime.hour) {
      alert('Please fill in all required fields (Date, In Time, Out Time)');
      return;
    }

    const workingHours = calculateWorkingHours(timeEntry.inTime, timeEntry.outTime, timeEntry.breakTime);

    setWorkRecords(prev => ({
      ...prev,
      [timeEntry.selectedDate]: {
        inTime: timeEntry.inTime,
        outTime: timeEntry.outTime,
        breakTime: timeEntry.breakTime,
        workingHours: workingHours
      }
    }));

    setTimeEntry(prev => ({
      ...prev,
      inTime: { hour: '', minute: '', period: 'AM' },
      outTime: { hour: '', minute: '', period: 'PM' },
      breakTime: 60
    }));

    setTimeStatus({
      inTimeSet: false,
      outTimeSet: false
    });

    alert('Work time added successfully!');
  };

  const handleDateClick = (day) => {
    if (!day) return;
    const dateStr = formatDate(day);
